, bp.shelflifeminpct AS c_bpartner_shelflifeminpct, bp.so_creditlimit AS c_bpartner_so_creditlimit, bp.socreditstatus AS c_bpartner_socreditstatus, bp.so_creditused AS c_bpartner_so_creditused, bp.so_description AS c_bpartner_so_description, bp.totalopenbalance AS c_bpartner_totalopenbalance, bp.updated AS c_bpartner_updated, bp.updatedby AS c_bpartner_updatedby, bp.url AS c_bpartner_url, bpgt.ad_org_id AS c_bpartner_greeting_ad_org_id, bpgt.isactive AS c_bpartner_greeting_isactive, bpg.isfirstnameonly AS c_bpartner_greeting_isfirstna, bpgt.name AS c_bpartner_greeting_name, bpl.ad_org_id AS c_bpartner_location_ad_org_id, bpl.created AS c_bpartner_location_created, bpl.createdby AS c_bpartner_location_createdby, bpl.c_salesregion_id, bpl.fax AS c_bpartner_location_fax, bpl.isactive AS c_bpartner_location_isactive, bpl.isbillto, bpl.isdn, bpl.ispayfrom, bpl.isremitto, bpl.isshipto, bpl.name AS c_bpartner_location_name, bpl.phone AS c_bpartner_location_phone, bpl.phone2 AS c_bpartner_location_phone2, bpl.updated AS c_bpartner_location_updated, bpl.updatedby AS c_bpartner_location_updatedby, l.address1, l.address2, l.address3, l.address4, l.ad_org_id AS c_location_ad_org_id, l.c_city_id, l.c_country_id, l.city, l.created AS c_location_created, l.createdby AS c_location_createdby, l.c_region_id, l.isactive AS c_location_isactive, l.postal_add, l.regionname, l.updated AS c_location_updated, l.updatedby AS c_location_updatedby, bpc.ad_org_id AS ad_user_ad_org_id, bpc.ad_orgtrx_id AS ad_user_ad_ad_orgtrx_id, bpc.birthday AS ad_user_ad_birthday, bpc.c_bpartner_id AS ad_user_c_bpartner_id, bpc.c_bpartner_location_id AS ad_user_c_bpartner_location_id, bpc.c_greeting_id AS ad_user_c_greeting_id, bpc.comments AS ad_user_comments, bpc.created AS ad_user_created, bpc.createdby AS ad_user_createdby, bpc.description AS ad_user_description, bpc.email AS ad_user_email, bpc.fax AS ad_user_fax, bpc.isactive AS ad_user_isactive, bpc.lastcontact AS ad_user_lastcontact, bpc.lastresult AS a