salesrep_bp_c_greeting_id, ubp.c_invoiceschedule_id AS salesrep_bp_invoiceschedule_id, ubp.c_paymentterm_id AS salesrep_bp_c_paymentterm_id, ubp.created AS salesrep_bpartner_created, ubp.createdby AS salesrep_bpartner_createdby, ubp.c_taxgroup_id AS salesrep_bp_c_taxgroup_id, ubp.deliveryrule AS salesrep_bpartner_deliveryrule, ubp.deliveryviarule AS salesrep_bpartner_deliveryvia, ubp.description AS salesrep_bpartner_description, ubp.dunninggrace AS salesrep_bpartner_dunninggrace, ubp.firstsale AS salesrep_bpartner_firstsale, ubp.flatdiscount AS salesrep_bpartner_flatdiscount, ubp.freightcostrule AS salesrep_bpartner_freightcost, ubp.invoicerule AS salesrep_bpartner_invoicerule, ubp.isactive AS salesrep_bpartner_isactive, ubp.iscustomer AS salesrep_bpartner_iscustomer, ubp.isdiscountprinted AS salesrep_bpartner_isdiscountp, ubp.isemployee AS salesrep_bpartner_isemployee, ubp.ismanufacturer AS salesrep_bpartner_ismanufactu, ubp.isonetime AS salesrep_bpartner_isonetime, ubp.ispotaxexempt AS salesrep_bpartner_ispotaxexem, ubp.isprospect AS salesrep_bpartner_isprospect, ubp.issalesrep AS salesrep_bpartner_issalesrep, ubp.issummary AS salesrep_bpartner_issummary, ubp.istaxexempt AS salesrep_bpartner_istaxexempt, ubp.isvendor AS salesrep_bpartner_isvendor, ubp.logo_id AS salesrep_bpartner_logo_id, ubp.m_discountschema_id AS salesrep_bp_mdiscountschema_id, ubp.m_pricelist_id AS salesrep_bp_m_pricelist_id, ubp.naics AS salesrep_bpartner_naics, ubp.numberemployees AS salesrep_bpartner_numberemplo, ubp.paymentrule AS salesrep_bpartner_paymentrule, ubp.paymentrulepo AS salesrep_bpartner_paymentrupo, ubp.po_discountschema_id AS salesrep_bp_po_discountscm_id, ubp.po_paymentterm_id AS salesrep_bp_po_paymentterm_id, ubp.po_pricelist_id AS salesrep_bp_po_pricelist_id, ubp.poreference AS salesrep_bpartner_poreference, ubp.potentiallifetimevalue AS salesrep_bpartner_potentialli, ubp.rating AS salesrep_bpartner_rating, ubp.salesrep_id AS salesrep_bpartner_salesrep_id, ubp.salesvolume AS salesrep_bpartner_salesvolume, ubp.sendemail AS salesrep_bpartner_sendemail, ubp.shareofcustomer AS salesrep_bpartner_shareofcust, ubp.shelflifeminpct AS salesrep_bpartner_shelflifemi, ubp.so_creditlimit AS salesrep_bpartner_so_creditli, ubp.socreditstatus AS salesrep_bpartner_socreditsta, ubp.so_creditused AS salesrep_bpartner_so_creditus, ubp.so_description AS salesrep_bpartner_so_descript, ubp.totalopenbalance AS salesrep_bpartner_totalopenba, ubp.updated AS salesrep_bpartner_updated, ubp.updatedby AS salesrep_bpartner_updatedby, ubp.url AS salesrep_bpartner_url
   FROM c_dunningrun dr
   JOIN c_dunningrunentry dre ON dr.c_dunningrun_id = dre.c_dunningrun_id
   JOIN c_dunninglevel dl ON dre.c_dunninglevel_id = dl.c_dunninglevel_id
   JOIN c_dunninglevel_trl dlt ON dl.c_dunninglevel_id = dlt.c_dunninglevel_id
   JOIN c_bpartner bp ON dre.c_bpartner_id = bp.c_bpartner_id
   LEFT JOIN c_greeting bpg ON bp.c_greeting_id = bpg.c_greeting_id
   LEFT JOIN c_greeting_trl bpgt ON bp.c_greeting_id = bpgt.c_greeting_id AND dlt.ad_language = bpgt.ad_language
   JOIN c_bpartner_location bpl ON dre.c_bpartner_location_id = bpl.c_bpartner_location_id
   JOIN c_location l ON bpl.c_location_id = l.c_location_id
   LEFT JOIN ad_user bpc ON dre.ad_user_id = bpc.ad_user_id
   LEFT JOIN c_greeting bpcg ON bpc.c_greeting_id = bpcg.c_greeting_id
   LEFT JOIN c_greeting_trl bpcgt ON bpc.c_greeting_id = bpcgt.c_greeting_id AND dlt.ad_language = bpcgt.ad_language
   JOIN ad_orginfo oi ON dr.ad_org_id = oi.ad_org_id
   JOIN ad_clientinfo ci ON dr.ad_client_id = ci.ad_client_id
   LEFT JOIN ad_user u ON dre.salesrep_id = u.ad_user_id
   LEFT JOIN c_bpartner ubp ON u.c_bpartner_id = ubp.c_bpartner_id;

