ubp.salesvolume AS bpartner_salesvolume, ubp.sendemail AS bpartner_sendemail, ubp.shareofcustomer AS bpartner_shareofcustomer, ubp.shelflifeminpct AS bpartner_shelflifeminpct, ubp.so_creditlimit AS bpartner_so_creditlimit, ubp.socreditstatus AS bpartner_socreditstatus, ubp.so_creditused AS bpartner_so_creditused, ubp.so_description AS bpartner_so_description, ubp.taxid AS bpartner_taxid, ubp.totalopenbalance AS bpartner_totalopenbalance, ubp.updated AS bpartner_updated, ubp.updatedby AS BPartner_UpdatedBy, ubp.url AS bpartner_url, ubp.value AS BPartner_Value, cur.cursymbol AS CurSymbol, cur.description AS cur_description FROM c_invoice i
JOIN c_doctype_trl dt ON i.c_doctype_id = dt.c_doctype_id
JOIN c_paymentterm pt ON i.c_paymentterm_id = pt.c_paymentterm_id
JOIN c_paymentterm_trl ptt ON i.c_paymentterm_id = ptt.c_paymentterm_id AND dt.ad_language = ptt.ad_language
JOIN c_bpartner bp ON i.c_bpartner_id = bp.c_bpartner_id
LEFT JOIN c_greeting bpg ON bp.c_greeting_id = bpg.c_greeting_id
LEFT JOIN c_greeting_trl bpgt ON bp.c_greeting_id = bpgt.c_greeting_id AND dt.ad_language = bpgt.ad_language
JOIN c_bpartner_location bpl ON i.c_bpartner_location_id = bpl.c_bpartner_location_id
JOIN c_location l ON bpl.c_location_id = l.c_location_id
LEFT JOIN ad_user bpc ON i.ad_user_id = bpc.ad_user_id
LEFT JOIN c_greeting bpcg ON bpc.c_greeting_id = bpcg.c_greeting_id
LEFT JOIN c_greeting_trl bpcgt ON bpc.c_greeting_id = bpcgt.c_greeting_id AND dt.ad_language = bpcgt.ad_language
JOIN ad_orginfo oi ON i.ad_org_id = oi.ad_org_id
JOIN ad_clientinfo ci ON i.ad_client_id = ci.ad_client_id
LEFT JOIN ad_user u ON i.salesrep_id = u.ad_user_id
LEFT JOIN c_bpartner ubp ON u.c_bpartner_id = ubp.c_bpartner_id
LEFT JOIN c_currency_trl cur ON i.c_currency_id = cur.c_currency_id AND dt.ad_language = cur.ad_language
;

